<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use App\SlideShowModel;use App\TypeRumahModel;use App\HargaModel;use App\User;use App\UlasanModel;use App\GaleryModel;use App\VisitorModel;class PublicController extends Controller{    public function order($slug)    {        $link = new VisitorModel();        $link->ip = $_SERVER['REMOTE_ADDR'];        $link->link = $_SERVER['REQUEST_URI'];        $link->jenis = 1;        $link->save();        return redirect('https://api.whatsapp.com/send?phone=6282283828164&text=%20Halloooo%20apakah%20bisa%20order%20rumah%20dengan%20'.$slug.'%20ini?');    }    public function svlink()    {        $link = new VisitorModel();        $link->ip = $_SERVER['REMOTE_ADDR'];        $link->link = $_SERVER['REQUEST_URI'];        $link->jenis = 0;        $link->save();        return $link;    }    public function index(){    	$slide = SlideShowModel::orderBy('id', 'desc')->paginate('5');    	$typesa = TypeRumahModel::inRandomOrder()->paginate(8);    	$ulasan = UlasanModel::inRandomOrder()->paginate(8);        $galery = GaleryModel::orderBy('id', 'desc')->paginate(8);        $this->svlink();           	    	return view('home.index', compact(    		'slide',    		'typesa',    		'ulasan',            'galery'    	));    }    public function detailtype($slug){        $this->svlink();    	$data = TypeRumahModel::where('slug', $slug)->first();    	return view('home.pages.section.type', compact('data'));    }    public function harga(){        $this->svlink();    	$data = HargaModel::all();    	return view('home.pages.section.harga', compact('data'));    }    public function lokasi(){        $this->svlink();    	return view('home.pages.section.lokasi');    }    public function kontak(){        $this->svlink();    	return view('home.pages.section.kontak');    }    public function dgalery($slug)    {        $this->svlink();        $data = GaleryModel::where('slug', $slug)->first();        return view('home.pages.section.dgalery', compact('data'));    }   }